      private void resetActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_resetActionPerformed
            sel1.setText(" ");
            sel2.setText(" ");
            sel3.setText(" ");
            selected=0;
      }//GEN-LAST:event_resetActionPerformed

      private void listMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_listMouseClicked
            if(selected==0){
                  sel1.setText(" ");
                  sel2.setText(" ");
                  sel3.setText(" ");
            }
            selected++;
            if(selected==1)
                  sel1.setText(list.getSelectedValue().toString());
            else if(selected==2)
                  sel1.setText(sel1.getText()+", "+list.getSelectedValue().toString());
            
            if(selected==3)
                  sel2.setText(list.getSelectedValue().toString());
            else if(selected==4)
                  sel2.setText(sel2.getText()+", "+list.getSelectedValue().toString());
            
            if(selected==5)
                  sel3.setText(list.getSelectedValue().toString());
            else if(selected==6)
                  sel3.setText(sel3.getText()+", "+list.getSelectedValue().toString());
            
            if(selected>6)
                  JOptionPane.showMessageDialog(this, "Maximum tags selection limit is 6.");
      }//GEN-LAST:event_listMouseClicked

