      private void addActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addActionPerformed
            String t=null;
            if(selected==0)
                  JOptionPane.showMessageDialog(this, "You must select tags first!");
            else {
                        t=sel1.getText();
                        if(sel2.getText().toString().length()>2)
                              t=t+", "+sel2.getText();
                        if(sel3.getText().toString().length()>2)
                              t=t+", "+sel3.getText();
                        Data.selectedtag=t;
                        setVisible(false);
                        Createb obj = new Createb("tag");
                        obj.setVisible(true);
            }
      }//GEN-LAST:event_addActionPerformed

